mapper.select:
SELECT a.*,b.REAL_NAME,c.PHONE,d.type,e.start_time,e.end_time FROM t_patrol_records a LEFT JOIN sys_admin_info b on a.inspector_id=b.ADMIN_ID LEFT JOIN sys_admin c on b.ADMIN_ID=c.ID LEFT JOIN t_patrol_path d on d.id=a.path_id LEFT JOIN t_patrol_plan e on e.id=a.plan_id WHERE a.deleted=0
AND DATE(a.create_time)=#{createTime}
AND a.inspector_id=#{inspectorId}

SELECT t2.community_name,t1.name as pointName  FROM t_inspection_item t1
LEFT JOIN t_community t2 on  t2.id=t1.community_id
WHERE t1.id=#{pointId}
